sql_template.query:
SELECT a.agenda_code, a.subject, a.sender_organization, a.decision_status, a.sent_time
FROM agenda_all a
WHERE a.agenda_panel = {panel} 
                AND a.sent_time BETWEEN {period_start} AND {period_end}
ORDER BY a.sent_time DESC

SELECT ac.agenda_code, ac.subject, ac.sender, ac.sent_time
FROM agenda_chair ac
WHERE ac.agenda_panel = {panel} 
                AND ac.sender_organization = 'Chair' 
                AND ac.sent_time BETWEEN {period_start} AND {period_end}
ORDER BY ac.sent_time DESC
